import useContext, {UNSAFE_createContext} from './useContext';
import useEffect from './useEffect';

test('should call effect properly', () => {
  const context = UNSAFE_createContext();
  useContext(context, () => {
    const f = jest.fn();
    useEffect(f);
    context.emit('disposing');
    expect(f).toBeCalledTimes(1);
  });
});
