import useRequest from './useRequest';
import useEffect from './useEffect';

export default function useHeaders() {
  return [useRequest().headers, setHeaders];
}

function setHeaders(headers) {
  useEffect(({res}) => {
    if (!res) {
      return;
    }
    if (!res.headers) {
      res.headers = {};
    }
    Object.assign(res.headers, headers);
  });
}

